<script        src="https://code.jquery.com/jquery-1.12.4.min.js"        integrity="sha256-ZosEbRLbNQzLpnKIkEdrPv7lOy9C27hHQ+Xp8a4MxAQ="        crossorigin="anonymous"></script><script>    jQuery(document).ready(function($){        $('input[name="obrashenie_type"]').change(function(){            if ($('input[name="obrashenie_type"]:checked').attr('value')=='jl'){                $('li#company-name').show();                $('input[name="company_name"]').val('');            }            else{                $('li#company-name').hide();                $('input[name="company_name"]').val('——————');            }        })    })</script><form name="contact_feedback" method="post" action="" class="fiz formobsv">    <div class="formradio">        <input type="radio" name="obrashenie_type" value="fl" checked/><label>Обращение граждан</label>    </div>    <div class="formradio">        <input type="radio" name="obrashenie_type" value="jl"/><label>Обращение юридического лица</label>    </div>    <section>        <li style="display: none" id="company-name">            <label>Наименование юридического лица</label>            <input type="text" name="namejl"/>        </li>        <li>            <label>Фамилия</label>            <input type="text" name="family"/>        </li>        <li>            <label>Имя</label>            <input type="text" name="name"/>        </li>        <li>            <label>Отчество</label>            <input type="text" name="fatername"/>        </li>    </section>    <section>        <li>            <label>Населенный пункт</label>            <input type="text" name="city"/>        </li>        <li>            <label>Улица</label>            <input type="text" name="street"/>        </li>        <li>            <label>Дом</label>            <input type="text" name="house"/>        </li>        <li>            <label>Квартира/Кабинет</label>            <input type="text" name="apartament"/>        </li>        <li>            <label>Адрес электронной почты</label>            <input type="text" name="email"/>        </li>        <li>            <label>Телефон</label>            <input type="text" name="phone"/>        </li>    </section>    <section>        <li>            <label>Тема</label>            <input type="text" name="theme"/>        </li>        <li>            <label>Сообщение</label>            <textarea type="text" name="message"></textarea>        </li>        *Все поля обязательны для заполнения    </section>    <input class="button btn-zapis btn-form" type="submit" value="Отправить"/></form>